import React from 'react';
import { Home, Zap, Droplets, Flame, UserPlus } from 'lucide-react';

const Navbar = ({ activeTab, setActiveTab }) => {
  // Ortak buton stili
  const buttonClass = (tabName) => 
    `flex items-center gap-2 px-5 py-2 rounded-full transition-all duration-300 font-medium ${
      activeTab === tabName 
        ? 'bg-emerald-600 text-white shadow-[0_0_15px_rgba(16,185,129,0.5)] transform scale-105' 
        : 'text-gray-400 hover:text-white hover:bg-white/10'
    }`;

  return (
    <nav className="bg-[#0f172a] border-b border-white/10 px-4 py-4 sticky top-0 z-50 backdrop-blur-md bg-opacity-90">
      <div className="max-w-7xl mx-auto flex flex-wrap justify-center items-center gap-4">

        <button onClick={() => setActiveTab('home')} className={buttonClass('home')}>
          <Home size={18} /> Ana Sayfa
        </button>

        <div className="w-px h-6 bg-white/10 hidden sm:block"></div> {/* Ayırıcı Çizgi */}

        <button onClick={() => setActiveTab('elektrik')} className={buttonClass('elektrik')}>
          <Zap size={18} className={activeTab === 'elektrik' ? 'fill-current' : ''} /> Elektrik
        </button>

        <button onClick={() => setActiveTab('su')} className={buttonClass('su')}>
          <Droplets size={18} className={activeTab === 'su' ? 'fill-current' : ''} /> Su
        </button>

        <button onClick={() => setActiveTab('dogalgaz')} className={buttonClass('dogalgaz')}>
          <Flame size={18} className={activeTab === 'dogalgaz' ? 'fill-current' : ''} /> Doğalgaz
        </button>

        <div className="flex-grow sm:flex-grow-0"></div> {/* Mobilde boşluk bırakır */}

        <button 
            onClick={() => {
                setActiveTab('kayit');
                const event = new CustomEvent("openKayitForm");
                window.dispatchEvent(event);
            }} 
            className={`ml-auto sm:ml-4 flex items-center gap-2 px-5 py-2 rounded-full border border-emerald-500/30 text-emerald-400 hover:bg-emerald-500/10 transition-all ${activeTab === 'kayit' ? 'bg-emerald-500/10' : ''}`}
        >
          <UserPlus size={18} /> Kayıt Ol
        </button>

      </div>
    </nav>
  );
};

export default Navbar;
