                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Area type="monotone" dataKey="value" stroke="#059669" fill="#10b981" />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>

                {/* TAHMÄ°N ALANI */}
                <div className="bg-indigo-900/50 rounded-3xl p-8 border border-white/20">
                    <div className="flex justify-between items-center">
                        <h4 className="text-2xl font-bold text-white">Yapay Zeka Tahmini</h4>
                        <button onClick={handleGetPrediction} disabled={predLoading} className="bg-white text-indigo-900 px-6 py-2 rounded-lg font-bold">
                            {predLoading ? "HesaplanÄ±yor..." : "Tahmin Al"}
                        </button>
                    </div>
                    {prediction && (
                        <div className="mt-4 text-white">
                            <p className="text-lg font-semibold">{prediction.mesaj}</p>
                            <div className="grid grid-cols-3 gap-4 mt-4">
                                <div className="bg-black/30 p-3 rounded">âš¡ {prediction.elektrik_tahmini} kWh</div>
                                <div className="bg-black/30 p-3 rounded">ðŸ’§ {prediction.su_tahmini} mÂ³</div>
                                <div className="bg-black/30 p-3 rounded">ðŸ”¥ {prediction.dogalgaz_tahmini} mÂ³</div>
                            </div>
                        </div>
                    )}
                </div>
              </div>
            )}
        </div>
      </div>
    </div>
  );
};

const DataCard = ({ icon: Icon, label, value, unit, color }) => {
   const colors = { yellow: "text-yellow-400 border-yellow-500/30", blue: "text-blue-400 border-blue-500/30", orange: "text-orange-400 border-orange-500/30" };
   return (<div className={`backdrop-blur-md rounded-2xl p-6 border ${colors[color]} bg-white/5`}><div className="flex items-center gap-4"><Icon className="w-8 h-8" /><div><p className="text-white/70">{label}</p><p className="text-2xl font-bold text-white">{value} {unit}</p></div></div></div>);
};

