  { mahalle: "AtaÅŸehir", elektrik: { ortalama: 380 }, su: { ortalama: 30 }, dogalgaz: { ortalama: 90 } },
  { mahalle: "SÃ¼rsÃ¼rÃ¼", elektrik: { ortalama: 410 }, su: { ortalama: 35 }, dogalgaz: { ortalama: 110 } },
  { mahalle: "Cumhuriyet", elektrik: { ortalama: 320 }, su: { ortalama: 25 }, dogalgaz: { ortalama: 80 } },
  { mahalle: "BahÃ§elievler", elektrik: { ortalama: 390 }, su: { ortalama: 40 }, dogalgaz: { ortalama: 100 } },
];

const HomePage = () => {
  // api ve context kullanmÄ±yoruz, bÃ¶ylece hata vermez.

  const [allData, setAllData] = useState([]);
  const [neighborhoodNames, setNeighborhoodNames] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedNeighborhood, setSelectedNeighborhood] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [showDropdown, setShowDropdown] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);

  const [prediction, setPrediction] = useState(null);
  const [predLoading, setPredLoading] = useState(false);

  const searchContainerRef = useRef(null);

  // Verileri yÃ¼kleme taklidi yapÄ±yoruz
  useEffect(() => {
    const fetchInitialData = () => {
        setLoading(true);
        // Sahte veriyi yÃ¼klÃ¼yoruz
        const data = MOCK_DATA;
        setAllData(data);
        setNeighborhoodNames(data.map(item => item.mahalle));
        setLoading(false);
    };
    // YarÄ±m saniye bekletip yÃ¼klÃ¼yoruz ki gerÃ§ekÃ§i olsun
    setTimeout(fetchInitialData, 500);
  }, []);

  // Tahmin alma taklidi yapÄ±yoruz
  const handleGetPrediction = () => {
    if (!selectedNeighborhood) return;
    setPredLoading(true);

    // 1.5 saniye sonra rastgele sonuÃ§ Ã¼ret
    setTimeout(() => {
        setPrediction({
            mesaj: "Mevsim normallerine gÃ¶re %10 artÄ±ÅŸ bekleniyor.",
            elektrik_tahmini: Math.floor(Math.random() * 500) + 300,
            su_tahmini: Math.floor(Math.random() * 50) + 20,
            dogalgaz_tahmini: Math.floor(Math.random() * 150) + 80,
        });
        setPredLoading(false);
    }, 1500);
  };

  const searchNeighborhoods = useCallback(
    (query) => neighborhoodNames.filter((n) => n.toLowerCase().includes(query.toLowerCase())),
    [neighborhoodNames]
  );

  const filteredNeighborhoods = useMemo(() => {
    if (!searchQuery.trim()) return [];
    return searchNeighborhoods(searchQuery).slice(0, 5);
  }, [searchQuery, searchNeighborhoods]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchContainerRef.current && !searchContainerRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const hydrateSelection = useCallback(
    (neighborhoodName) => {
      if (!neighborhoodName) return;
      setPrediction(null);

      const foundData = allData.find(d => d.mahalle === neighborhoodName);

      if (foundData) {
        const payload = {
          name: foundData.mahalle,
          electricity: foundData.elektrik.ortalama,
          water: foundData.su.ortalama,
          gas: foundData.dogalgaz.ortalama,
        };
        setSelectedNeighborhood(payload);
        setSearchQuery(payload.name);
      }
      setShowDropdown(false);
      setHighlightedIndex(-1);
    },
    [allData]
  );

  const handleSelectNeighborhood = (neighborhoodName) => {
    hydrateSelection(neighborhoodName);
  };

  const handleKeyDown = (e) => {
    if (!showDropdown || filteredNeighborhoods.length === 0) return;
    if (e.key === "Enter" && highlightedIndex >= 0) {
      handleSelectNeighborhood(filteredNeighborhoods[highlightedIndex]);
    } else if (e.key === "ArrowDown") {
        setHighlightedIndex(p => p < filteredNeighborhoods.length - 1 ? p + 1 : p);
    } else if (e.key === "ArrowUp") {
        setHighlightedIndex(p => p > 0 ? p - 1 : -1);
    }
  };

  // AÃ§Ä±lÄ±ÅŸta otomatik Ã‡aydaÃ§Ä±ra seÃ§ilsin
  useEffect(() => {
    if (!loading && neighborhoodNames.length > 0 && !selectedNeighborhood) {
      handleSelectNeighborhood("Ã‡aydaÃ§Ä±ra");
    }
  }, [loading, neighborhoodNames, selectedNeighborhood, handleSelectNeighborhood]);

  const statsDisplay = [
    { icon: MapPin, label: "Toplam Mahalle", value: neighborhoodNames.length.toString(), color: "emerald" },
    { icon: Database, label: "Veri KaydÄ±", value: "Demo Mod", color: "purple" },
    { icon: TrendingUp, label: "Sistem", value: "Aktif", color: "orange" },
    { icon: Clock, label: "Son Veri", value: "Åžimdi", color: "rose" },
  ];

  const getColorClasses = (color) => {
    const colors = {
      emerald: "bg-emerald-50 text-emerald-600 border-emerald-200",
      purple: "bg-purple-50 text-purple-600 border-purple-200",
      orange: "bg-orange-50 text-orange-600 border-orange-200",
      rose: "bg-rose-50 text-rose-600 border-rose-200",
    };
    return colors[color] || colors.emerald;
  };

  if (loading) return <div className="text-gray-600 text-center pt-20">YÃ¼kleniyor...</div>;

  return (
    <div className="relative min-h-screen overflow-hidden bg-gray-900">
      <div className="absolute inset-0 bg-gradient-to-br from-gray-900 via-emerald-900 to-black"></div>

      {/* Video varsa Ã§alÄ±ÅŸÄ±r, yoksa hata vermez */}
      <video className="absolute inset-0 w-full h-full object-cover opacity-40" src={bgVideo} autoPlay loop muted onError={(e) => e.target.style.display='none'} />

      <div className="relative z-10 pt-10 px-4 sm:px-6 lg:px-8 max-w-7xl mx-auto pb-16">
        <div className="text-center mb-16 mt-8">
            <h1 className="text-5xl font-extrabold text-white mb-4">Åžehir TÃ¼ketim Analizi</h1>
            <p className="text-xl text-gray-200">Mahalle bazÄ±nda demo veriler.</p>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-16">
            {statsDisplay.map((stat, index) => {
              const Icon = stat.icon;
              return (
                <div key={index} className="bg-white/10 backdrop-blur-md rounded-3xl p-7 border border-white/20">
                  <div className={`w-14 h-14 rounded-full flex items-center justify-center mb-5 border-2 ${getColorClasses(stat.color)}`}><Icon className="w-7 h-7" /></div>
                  <h3 className="text-md font-medium text-gray-200 mb-1">{stat.label}</h3>
                  <p className="text-3xl font-bold text-white">{stat.value}</p>
                </div>
              );
            })}
        </div>

        <div id="search-section" ref={searchContainerRef} className="bg-white/10 backdrop-blur-xl rounded-3xl p-8 border border-white/20">
            <h2 className="text-3xl font-bold text-white mb-8 flex items-center gap-3"><Search className="w-8 h-8 text-emerald-400" /> DetaylÄ± Analiz</h2>

            <div className="relative mb-8">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => { setSearchQuery(e.target.value); setShowDropdown(true); }}
                onKeyDown={handleKeyDown}
                placeholder="Mahalle adÄ± girin (Ã–rn: Ã‡aydaÃ§Ä±ra)"
                className="w-full pl-5 pr-5 py-4 text-lg border border-white/30 rounded-2xl bg-white/10 text-white focus:outline-none focus:border-emerald-400"
              />
              {showDropdown && filteredNeighborhoods.length > 0 && (
                <div className="absolute w-full mt-2 bg-gray-900/95 backdrop-blur-xl rounded-2xl border border-white/10 max-h-64 overflow-y-auto z-50">
                  {filteredNeighborhoods.map((n, index) => (
                    <button key={n} onClick={() => handleSelectNeighborhood(n)} className={`w-full text-left px-6 py-4 hover:bg-emerald-900/50 text-white border-b border-white/10 ${index === highlightedIndex ? 'bg-emerald-900/50' : ''}`}>
                      {n}
                    </button>
                  ))}
                </div>
              )}
            </div>

            {selectedNeighborhood && (
              <div className="animate-fade-in">
                <div className="flex items-center gap-4 mb-8 pb-4 border-b border-white/10">
                   <div className="p-3 bg-emerald-500/20 rounded-xl"><MapPin className="w-8 h-8 text-emerald-400" /></div>
                   <div><h3 className="text-4xl font-bold text-white">{selectedNeighborhood.name}</h3></div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                  <DataCard icon={Zap} label="Elektrik" value={selectedNeighborhood.electricity} unit="kWh" color="yellow" />
                  <DataCard icon={Droplets} label="Su" value={selectedNeighborhood.water} unit="mÂ³" color="blue" />
                  <DataCard icon={Flame} label="DoÄŸalgaz" value={selectedNeighborhood.gas} unit="mÂ³" color="orange" />
                </div>

                <div className="bg-white rounded-3xl p-6 shadow-xl mb-8">
                  <h4 className="text-lg font-bold text-gray-800 mb-6">TÃ¼ketim GrafiÄŸi</h4>
                  <ResponsiveContainer width="100%" height={300}>
                    <AreaChart data={[
                        { name: "Elektrik", value: Number(selectedNeighborhood.electricity) }, 
                        { name: "Su", value: Number(selectedNeighborhood.water) }, 
                        { name: "DoÄŸalgaz", value: Number(selectedNeighborhood.gas) }
                    ]}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Area type="monotone" dataKey="value" stroke="#059669" fill="#10b981" />
                    </AreaChart>
                  </ResponsiveContainer>
                </div>

                <div className="bg-indigo-900/50 rounded-3xl p-8 border border-white/20">
                    <div className="flex justify-between items-center">
                        <h4 className="text-2xl font-bold text-white">Yapay Zeka Tahmini (Demo)</h4>
                        <button onClick={handleGetPrediction} disabled={predLoading} className="bg-white text-indigo-900 px-6 py-2 rounded-lg font-bold">
                            {predLoading ? "HesaplanÄ±yor..." : "Tahmin Al"}
                        </button>
                    </div>
                    {prediction && (
                        <div className="mt-4 text-white animate-fade-in">
                            <p className="text-lg font-semibold">{prediction.mesaj}</p>
                            <div className="grid grid-cols-3 gap-4 mt-4">
                                <div className="bg-black/30 p-3 rounded text-center">âš¡ {prediction.elektrik_tahmini} kWh</div>
                                <div className="bg-black/30 p-3 rounded text-center">ðŸ’§ {prediction.su_tahmini} mÂ³</div>
                                <div className="bg-black/30 p-3 rounded text-center">ðŸ”¥ {prediction.dogalgaz_tahmini} mÂ³</div>
                            </div>
                        </div>
                    )}
                </div>
              </div>
            )}
        </div>
      </div>

      <button
        onClick={() => {
          const event = new CustomEvent("openKayitForm");
          window.dispatchEvent(event);
        }}
        className="fixed bottom-6 right-6 z-40 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-4 rounded-full shadow-lg font-semibold flex items-center gap-2 transition-all hover:scale-105"
      >
        <Shield className="w-5 h-5" />
        KayÄ±t Ol
      </button>
    </div>
  );
};

const DataCard = ({ icon: Icon, label, value, unit, color }) => {
   const colors = { yellow: "text-yellow-400 border-yellow-500/30", blue: "text-blue-400 border-blue-500/30", orange: "text-orange-400 border-orange-500/30" };
   return (<div className={`backdrop-blur-md rounded-2xl p-6 border ${colors[color]} bg-white/5`}><div className="flex items-center gap-4"><Icon className="w-8 h-8" /><div><p className="text-white/70">{label}</p><p className="text-2xl font-bold text-white">{value} {unit}</p></div></div></div>);
};

export default HomePage;
